{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.0.0",
        "id": "123456abcd",
        "label": "Basic TCP",
        "remark": "Basic TCP Example with monitor",
        "TCP_LB_stack_1": {
            "class": "Tenant",
            "ssh_app": {
                "class": "Application",
                "template": "tcp",
                "serviceMain": {
                    "class": "Service_TCP",
                    "virtualAddresses": [
                        "10.1.10.50"
                    ],
                    "virtualPort": 22,
                    "pool": "ssh_pool"
                },
                "ssh_pool": {
                    "class": "Pool",
                    "monitors": [
                        {
                            "use": "tcp_half_monitor_30sec"
                        }
                    ],
                    "members": [
                        {
                            "servicePort": 22,
                            "serverAddresses": [
                                "10.1.10.100",
                                "10.1.10.101"
                            ]
                        }
                    ]
                },
                "tcp_half_monitor_30sec": {
                    "class": "Monitor",
                    "monitorType": "tcp-half-open",
                    "interval": 30,
                    "timeout": 91,
                    "adaptive": false
                }
            }
        }
    }
}

